import {App} from '../App'
import { Statistic, Title, List, Item, Label, Percentage } from "./Statistics.styled";

export const Statistics = ({ title, stats }) => {
    return (
        <Statistic>

            {title && <Title>{title}</Title>}

            <List >
                {stats.map((stats) => (
                    <Item key={stats.id}>
                        <Label >{stats.label}</Label>
                        <Percentage >{stats.percentage}%</Percentage>
                    </Item>

                ))}

            </List>
        </Statistic>
    )
}
